// Recupera i dati dagli input
const array = Inputs.Ricavi;
const isGiornaliero = Inputs.Giornaliero;

// Oggetto per tenere traccia degli importi aggregati per periodo
const totaliRaggruppati = {};

// Raggruppa i dati in base al valore di `isGiornaliero`
if (isGiornaliero) {
    // Aggrega gli importi per giorno
    array.forEach(item => {
        const giorno = item.data; // Estrae l'intera data (giorno, mese, anno)
        if (totaliRaggruppati[giorno]) {
            totaliRaggruppati[giorno] += item.importo;
        } else {
            totaliRaggruppati[giorno] = item.importo;
        }
    });
} else {
    // Aggrega gli importi per mese
    array.forEach(item => {
        const mese = item.data.substring(0, 7); // Estrae solo anno e mese dalla data (es. 2024-04)
        if (totaliRaggruppati[mese]) {
            totaliRaggruppati[mese] += item.importo;
        } else {
            totaliRaggruppati[mese] = item.importo;
        }
    });
}

// Ottieni le etichette dei periodi e i dati degli importi
const labels = Object.keys(totaliRaggruppati).sort(); // Ordina le etichette in ordine crescente
const data = Object.values(totaliRaggruppati); // Ottieni gli importi aggregati

// Prepara i dati per Chart.js
const datiGrafico = {
    labels,
    datasets: [
        {
            label: isGiornaliero ? 'Totali Giornalieri' : 'Totali Mensili',
            data: data,
            borderColor: 'blue',
            backgroundColor: 'blue',
        }
    ]
};

Outputs.Data = datiGrafico;

