const supabase = Noodl.Variables.supabase;

if (!supabase) {
    console.error('Supabase client not found');
    Outputs.Failure();
    return;
}

(async () => {
    try {
        // Definizione delle variabili per i filtri e l'ordinamento
        const var_when_soggetto = Inputs.Var_when_soggetto;
        const var_when_gestore = Inputs.Var_when_gestore;
        const var_when_area = Inputs.Var_when_area;
        const var_when_ticket = Inputs.Var_when_ticket;
        const var_orderby_column = Inputs.Var_orderby_column;
        const var_orderby_desc_asc = Inputs.Var_orderby_desc_asc;
        const dataDal = Inputs.DataDal;
        const dataAl = Inputs.DataAl;

        // Inizia a costruire la query
        let queryRicavi = supabase
            .from('v_ricavi_fees')
            .select('supid:id, date, importo, an_area, an_area_nome, an_tecnologia, an_tecnologia_nome, an_gestore, an_gestore_ragsoc, fees_gestore, soggetto, soggetto_ragsoc, an_prodotto, prodotto_nome, an_setter, fees_setter, an_salescoach, fees_salescoach, an_leadgen, fees_leadgen');

        // Applica la clausola `where` condizionalmente
        const matchConditions = {};
        if (var_when_area) matchConditions.an_area = var_when_area;
        if (var_when_ticket) matchConditions.an_tecnologia = var_when_ticket;
        if (var_when_soggetto) matchConditions.soggetto = var_when_soggetto;
        if (var_when_gestore) matchConditions.an_gestore = var_when_gestore;

        queryRicavi = queryRicavi.match(matchConditions);

        // Applica la clausola `order by` condizionalmente
        if (var_orderby_column && var_orderby_desc_asc) {
            queryRicavi = queryRicavi.order(var_orderby_column, { ascending: var_orderby_desc_asc === 'asc' });
        }

        // Applica un `order by` per la colonna 'date'
        queryRicavi = queryRicavi.order('date', { ascending: true });

        // Applica i filtri `dataDal` e `dataAl` condizionalmente
        if (dataDal) {
            queryRicavi = queryRicavi.gte('date', dataDal);
        }

        if (dataAl) {
            queryRicavi = queryRicavi.lte('date', dataAl);
        }

        // Esegue la query
        const { data, error } = await queryRicavi;

        if (error) {
            console.error(error);
            Outputs.Failure();
        } else {
            Outputs.Data = data;

            // Calcola il fatturato
            const fatturato = data.reduce((acc, curr) => acc + (curr.importo || 0), 0);

            let feesGestore, feesSetter, feesSalescoach, feesLeadgen;
            if (var_when_gestore) {
                // Filtra le fee per gestore specifico
                feesGestore = data.filter(row => row.an_gestore === var_when_gestore).reduce((acc, curr) => acc + (curr.fees_gestore || 0), 0);
                feesSetter = data.filter(row => row.an_setter === var_when_gestore).reduce((acc, curr) => acc + (curr.fees_setter || 0), 0);
                feesSalescoach = data.filter(row => row.an_salescoach === var_when_gestore).reduce((acc, curr) => acc + (curr.fees_salescoach || 0), 0);
                feesLeadgen = data.filter(row => row.an_leadgen === var_when_gestore).reduce((acc, curr) => acc + (curr.fees_leadgen || 0), 0);
            } else {
                // Calcola le fee totali senza filtro per gestore
                feesGestore = data.reduce((acc, curr) => acc + (curr.fees_gestore || 0), 0);
                feesSetter = data.reduce((acc, curr) => acc + (curr.fees_setter || 0), 0);
                feesSalescoach = data.reduce((acc, curr) => acc + (curr.fees_salescoach || 0), 0);
                feesLeadgen = data.reduce((acc, curr) => acc + (curr.fees_leadgen || 0), 0);
            }

            const feesTotali = feesGestore + feesSetter + feesSalescoach + feesLeadgen;

            // Assegna gli output
            Outputs.Fatturato = fatturato;
            Outputs.FeesVenditore = feesGestore;
            Outputs.FeesSetter = feesSetter;
            Outputs.FeesSalescoach = feesSalescoach;
            Outputs.FeesLeadgen = feesLeadgen;
            Outputs.FeesTotali = feesTotali;
            Outputs.Success();
        }
    } catch (error) {
        console.error(error);
        Outputs.Failure();
    }
})();

