        Outputs.FeesLeadgen = data.filter(row => row.an_leadgen === var_when_gestore).reduce((acc, curr) => acc + curr.fees_leadgen, 0);
        Outputs.FeesTotali = Outputs.FeesVenditore + Outputs.FeesSetter + Outputs.FeesSalescoach + Outputs.FeesLeadgen;
        Outputs.Success();
    }
} catch (error) {
    console.error(error);
    Outputs.Failure();
}

